import React from "react"
import styled from "styled-components"
import { useBreakpoint } from "gatsby-plugin-breakpoints"

const Wrapper = styled.div`
  position: relative;
  width: 100%;
`

const Content = styled.div`
  padding: 15px;
  width: 100%;
  max-width: 1200px;
  z-index: 9;
  display: flex;
  justify-content: ${props => (props.showIcons ? "space-between" : "center")};
  margin: 0 auto;

  path {
    fill: #fff;
  }

  @media (min-width: 1300px) {
    align-items: center;
  }
`

const Bar = styled.div`
  color: #fff;
  width: 100%;
  max-width: 1200px;

  @media (min-width: 1300px) {
    display: flex;
    align-items: center;
    width: 70%;
  }
`

const BarItem = styled.div`
  display: flex;
  align-items: center;
  padding: 5px;

  svg {
    height: 40px;
    margin-right: 4px;
  }

  @media (min-width: 1300px) {
    padding: 10px 10px 10px 0;
    margin-right: 15px;
  }
`

const GalerieBtn = styled.button`
  display: flex;
  align-items: center;
  background: rgba(12, 178, 247, 0.46);
  border-radius: 0;
  border: none;
  border-radius: 0;
  color: #fff;
  padding: 10px;
  transition: 0.3s all ease-out;
  margin-top: auto;

  svg {
    height: 30px;
  }

  &:hover {
    cursor: pointer;
    background: rgba(12, 178, 247, 1);
  }

  @media (min-width: 1300px) {
    padding: 8px 26px;

    svg {
      margin-left: 15px;
    }
  }
`

